<?phpsession_start();$diretorio = '../temp/'; //diretorio dos arquivos enviados//montando o caminho para onde o arquivo enviado vai ser copiado$nome_imagem1    = $_FILES['arquivo1']['name'];$ext = strtolower(strrchr($nome_imagem1,"."));$nome_atual1 = md5(uniqid(time())).$ext; $caminho_arquivo1 = $diretorio .basename($_FILES['arquivo1']['name']);$_SESSION['fototemp1']=$_FILES['arquivo1']['name'];//a funcao php move_uploaded_file copia os arquivos enviados para o caminho que você desejar if (move_uploaded_file($_FILES['arquivo1']['tmp_name'], $caminho_arquivo1)) { 	  echo "sucesso";//este 'sucesso' é o valor que vai estar no parametro resposta no código js} else {	echo "erro";//em caso de erro na copia do arquivo reposta vai ter o valor 'erro'}?>